import React, { Component } from 'react';
import CreateTask from './Tasks/CreateTask'
import ManageTasks from './Tasks/ManageTasks'
import * as reactbootstrap from 'react-bootstrap';
import { Constants } from './commonData/Constants';
// import Moment from 'react-moment';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: Constants.Tasks,
    }

    this.updateTasks = this.updateTasks.bind(this);
  }

  updateTasks(updatedTasks, newTask = 1) {
    if (newTask === 1) {
      let preCount = (localStorage.getItem('count') !== undefined) ? Number(localStorage.getItem('count')) : 0
      localStorage.setItem('count', preCount + 1)
    } else {
      let preCount = (localStorage.getItem('checkboxCount') !== undefined) ? Number(localStorage.getItem('checkboxCount')) : 0
      localStorage.setItem('checkboxCount', preCount + 1)
    }

    this.setState({
      tasks: updatedTasks,
    })
  }

  render() {
    const today = new Date()
    let dateTime = today.toLocaleString('default', { month: 'long' })
//     let todayDate = new Date();
//     let dateTime = todayDate.toLocaleString();
//     const today = new Date() today. toLocaleString('default', { month: 'long' }) ...
// today. toLocaleString('default', { month: 'short' }) ...
// const today = new Date() today. toLocaleString('it-IT', { month: 'long' })

    let curTime = Date()
    var str = "Wed Oct 28 2020 04:10:29 GMT+0530 (India Standard Time)";
    var splitted = str.split('GMT'); //this will output ["1234", "56789"]
    var first = splitted[0]; //"1234"
    var second = splitted[1]; //"56789"
    console.log('First is: ' + first + ', and second is: ' + second);
    // var date = new Date(curTime),
    // mnth = ("0" + (date.getMonth() + 1)).slice(-2),
    // day = ("0" + date.getDate()).slice(-2);
    // let presentdate =  [date.getFullYear(), mnth, day].join("-");
    // console.log(presentdate);
    //date1 = new Date ( "January 6, 2013" );
    // date = date1.getDate();
    // year = date1.getFullYear();
    // month = date1.getMonth();
    return (
      <div style={{ width: '100%', border: '3px solid #fff', padding: '20px'}}>
        {dateTime}
        <p>Current Time : {curTime} {first}</p>
        <div style={{ width: '35%', float: 'left', padding: '20px', border: '2px solid black', marginRight: '10px'}}>
          <CreateTask tasks = {this.state.tasks} updateTasks={this.updateTasks}/>
        </div>
        <div style={{ width: '35%', float: 'left', padding: '20px', border: '2px solid black'}}>
          <ManageTasks tasks = {this.state.tasks} count = {localStorage.getItem('count')} updateTasks={this.updateTasks} checkboxCount={localStorage.getItem('checkboxCount')}/>
        </div>
      </div>
    );
  }
}

export default App;
