import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import DateTimePicker from 'react-datetime-picker';
import { Constants } from '../commonData/Constants';

class CreateTask extends Component {
  constructor(props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      taskTypes: Constants.Types,
      type: '',
      DateTime: null,
    }
  }

  handleChange() {

  }

  render() {
    console.log(this.state.taskTypes);
    const { title, description, type, taskTypes, DateTime } = this.state
    return(
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: '#EC661C', fontSize: '20px'}} >
            <span><h4>{'Create task'}</h4></span>
        </div>
        <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
           <reactbootstrap.FormGroup>
             <div style={{ marginbottom: '15px',border: '0px' }}>
               <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Title'}<span style={{ color: 'red' }}> * </span></label>
               <input
                 name="title"
                 value={title}
                 type="text"
                 style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px'}}
                 placeholder={"Task title"}
                 onChange={this.handleChange}
                 autoComplete="off" />
             </div>
             {/* {emailRequired === true &&
               <div style={{ color: 'red', fontSize: '15px' }}>{"userName field is required"}</div>
             }
             {validEmail === false &&
               <div style={{ color: 'red', fontSize: '15px' }}>{"Please enter a proper Email-id"}</div>
             } */}
           </reactbootstrap.FormGroup>
           <reactbootstrap.FormGroup>
             <div style={{ marginbottom: '15px',border: '0px' }}>
               <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Description'}<span style={{ color: 'red' }}> * </span></label>
               <input
                 name="description"
                 value={description}
                 type="textarea"
                 style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px'}}
                 placeholder={"description"}
                 onChange={this.handleChange}
                 autoComplete="off" />
             </div>
           </reactbootstrap.FormGroup>
           <reactbootstrap.FormGroup>
             <div style={{ marginbottom: '15px',border: '0px' }}>
               <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Description'}<span style={{ color: 'red' }}> * </span></label>
                   <reactbootstrap.FormControl as="select" name="type"
                       className="input_sw"
                       value={type}
                       onChange={this.handleChange} >
                       <option>Select</option>
                       {Object.values(taskTypes).map(taskType => <option value={taskType.name}>{taskType.name}</option>)}
                   </reactbootstrap.FormControl>
             </div>
           </reactbootstrap.FormGroup>
           <reactbootstrap.FormGroup>
             <DateTimePicker
               onChange={onChange}
               value={DateTime}
             />
           </reactbootstrap.FormGroup>
           {/* <InputGroup.Prepend>
             <InputGroup style={{ padding: '8px', color: '#EC661C' }} id="basic-addon1">{t('Description')}:</InputGroup>
           </InputGroup.Prepend>
           </div>
           <div class="col-md-8 input-padd">
           <FormControl
             style={{ height: '100px' }}
             as="textarea" rows="3"
             placeholder={this.props.placeholder !== undefined ? t(this.props.placeholder) : ''}
             value={this.state.description}
             onChange={e => this.setState({ description: e.target.value })}
             className="input_sw"
           /> */}
           <div>
             <reactbootstrap.Button style={{ float: 'left', backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={e => this.logOut()}>
                   {'Add'}
               </reactbootstrap.Button>
           </div>
         </reactbootstrap.Form>
       </reactbootstrap.Container>
    );
  }
}

export default (CreateTask);
