import React, { Component } from 'react';
import CreateTask from './Tasks/CreateTask'
import ManageTasks from './Tasks/ManageTasks'
import * as reactbootstrap from 'react-bootstrap';
import { Constants } from './commonData/Constants';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: Constants.Tasks,
    }

    this.updateTasks = this.updateTasks.bind(this);
  }

  updateTasks(updatedTasks) {
    let preCount = (localStorage.getItem('count') !== undefined) ? Number(localStorage.getItem('count')) : 0
    localStorage.setItem('count', preCount + 1)
    this.setState({
      tasks: updatedTasks,
    })
  }

  render() {
    return (
      <div style={{ width: '100%', border: '3px solid #fff', padding: '20px'}}>
        <div style={{ width: '40%', float: 'left', padding: '20px', border: '2px solid black', marginRight: '10px'}}>
          <CreateTask tasks = {this.state.tasks} updateTasks={this.updateTasks}/>
        </div>
        <div style={{ width: '40%', float: 'left', padding: '20px', border: '2px solid black'}}>
          <ManageTasks tasks = {this.state.tasks} count = {localStorage.getItem('count')} updateTasks={this.updateTasks}/>
        </div>
      </div>
    );
  }
}

export default App;
